module.exports = function(grunt) {
    var xml = require('node-xml-lite');

    var defaultNgTemplate = {
        release: {
            cwd: 'src/versions/<%= grunt.task.current.args[0] %>',
            src: ['partials/**/*.html', 'js/**/*.html' ],
            dest: 'www/versions/<%= grunt.task.current.args[0] %>/js/partials.js',
            options: {
                module: xml.parseString(grunt.file.read('config.xml')).attrib.id,
                standalone: false
            }
        }
    };

    return defaultNgTemplate;
};
