module.exports = function (grunt) {

	var browserList = {
		browsers: ['last 2 versions', 'ie >= 10', 'Android >= 4.1', 'iOS >= 8' ]
	};

	var cssnanoModules = {
		autoprefixer : {
			browsers: browserList.browsers
		},
		safe : true
	};

    return {
	    development: {
			options: {
				map: true, // inline sourcemaps
				processors: [
					require('autoprefixer')(browserList), // add vendor prefixes
				]
			},
			src :  'www/versions/<%= grunt.task.current.args[0] %>/css/*.css',
	    },
	    release: {
			options: {
	            map : true,
				processors : [
					require('autoprefixer')(browserList),
					require('cssnano')(cssnanoModules)
				]
	        },
			src :  'www/versions/<%= grunt.task.current.args[0] %>/css/*.css'
	    }
	};
};
