module.exports = function(grunt) {

    grunt.registerTask('bowerInstall', 'installs all the bower dependencies in the shell and version folders', function () {
        grunt.task.run('shell:bowerInstallShared');

        for (var versionIndex = 0; versionIndex < grunt.config('config').supportedServices.length; versionIndex++) {
            var currentVersion = grunt.config('config').supportedServices[versionIndex];

            grunt.task.run('shell:bowerInstallVersion:' + currentVersion);
        }
    });
};
