module.exports = function (grunt) {

    grunt.registerTask('build', 'Replaces the old "build" alias.', function (targettedVersion) {
        grunt.task.run('bowerInstall');
        grunt.task.run('transformJSON:development');
        grunt.task.run('sync:syncRoot');
        grunt.task.run('sync:syncShared');
        grunt.task.run('syncSelection:' + targettedVersion);
        grunt.task.run('htmlbuildSelection:' + targettedVersion);
        grunt.task.run('postcssSelection:' + targettedVersion);
    });

};
