module.exports = function (grunt) {

    grunt.registerTask('checkServices', 'Checks if all services supported are actually implemented', function () {

        var servicesSupportedArr = grunt.config('config').supportedServices;

        servicesSupportedArr.forEach(function (service) {
            if (!grunt.file.exists('src/versions/' + service)) {
                grunt.warn('no src map for this service found : ' + service);
            }

        });

    });
};