module.exports = function (grunt) {

    grunt.task.registerTask('copyAntProps', 'copies the ant.properties into the android project', function () {

        var fs = require('fs');

        // no need to configure below
        var rootdir = process.cwd();
        var srcfile = grunt.path.join(rootdir, "config/android/ant.properties");
        var destfile = grunt.path.join(rootdir, "platforms/android/ant.properties");

        var destdir = grunt.path.dirname(destfile);
        if (fs.existsSync(srcfile) && fs.existsSync(destdir)) {
            fs.createReadStream(srcfile).pipe(
               fs.createWriteStream(destfile));
        }
    });

};
