module.exports = function (grunt) {

    grunt.registerTask('serve', 'Replaces the old "serve" alias.', function (targettedVersion) {
        var option = grunt.option('targetVersion');
        if (option) {
            targettedVersion = grunt.config('config').targettedService;
        }

        grunt.task.run('checkServices');
        grunt.task.run('build:' + targettedVersion);
        grunt.task.run('browserSync');
        grunt.task.run('watch:all:' + targettedVersion);
    });

};