module.exports = function (grunt) {

    grunt.registerTask('syncSelection', 'Interface for sync:development, allows single or complete syncing of the files', function (targettedVersion) {
        var targettedVersions = [];
        if (targettedVersion === 'undefined' || typeof targettedVersion === 'undefined') { //check on string here as well
            targettedVersions = grunt.config('config').supportedServices;
        } else {
            targettedVersions.push(targettedVersion);
        }
        targettedVersions.forEach(function (version) {
            grunt.task.run('sync:targettedVersion:' + version);
        });
    });

};