var fs = require('fs-extra');
var src_folder = process.cwd() + '/' + process.argv[2];
var target_folder = process.cwd() + '/../../..';
var exec = require('child_process').exec;

var easyLogBinder = function(dir) {
	return function(err) {
		if (err) {
			return console.error(err);
		}
		console.log("moved " + src_folder + " with success to new location " + target_folder);
	};
};

fs.readdir(src_folder, function(err, foundNames) {
	 for (var index = 0; index < foundNames.length; ++index) {
		var dir = foundNames[index];
	 	var dirPath = src_folder + '/' + dir;
		fs.move(src_folder + "/" + dir, target_folder + "/" + dir, easyLogBinder(dir));
	 }
 });

var cmd = 'brew install dos2unix';

exec(cmd, function(error, stdout, stderr) {
   console.log('dos2unix installed');
});
