module.exports = function (grunt) {

	var browserList = {
		browsers: ['last 2 versions', 'ie >= 10']
	};

    return {
	    development: {
			options: {
				map: true, // inline sourcemaps
				processors: [
					require('autoprefixer')(browserList), // add vendor prefixes
				]
			},
			src :  'www/css/*.css',
	    },
	    release: {
			options: {
	            map : true,
				processors : [
					require('autoprefixer')(browserList),
					require('cssnano')()
				]
	        },
			src :  'www/css/*.css',
	    }
	};
};
