module.exports = function (grunt) {
	grunt.registerMultiTask('setMode', 'Sets the application in a certain mode ("debug" or "release")', function () {
        var mode = this.target;
        this.files.forEach(function (file) {
            var srcPath = file.src;
            var destPath = file.dest;
            var jsonSrcFile = grunt.file.readJSON(srcPath);
            jsonSrcFile.mode = mode;
			jsonSrcFile.buildDate = new Date().getTime();
            grunt.file.write(destPath, JSON.stringify(jsonSrcFile));
        });
    });
};
